
public class IfTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean condition = true;
		//boolean condition = false;
		
		if(condition) {
			System.out.println("조건은 참!");
			//条件が真なら実行される部分
			
		}
		else {
			System.out.println("조건은 거짓!");
			//条件が偽れば実行される部分。
		}

	}

}
